mapper.select:
select

from t_bic_celebrity



    and (name like #{name} or simple_spell like #{name} or full_spell like #{name})



    and simple_spell like #{simpleSpell}



    and full_spell like #{fullSpell}


    and category = #{category,jdbcType=VARCHAR}


    and type = #{type,jdbcType=VARCHAR}


    and nation like #{nation}



      and status = #{examineStatus,jdbcType=INTEGER}


      and (status = 1 or status = 9)




      and status = #{onlineStatus,jdbcType=INTEGER}


      and 9 > status

mapper.update:
update t_bic_celebrity
set updated_date = #{updatedDate,jdbcType=TIMESTAMP},
updated_user = #{updatedUser,jdbcType=VARCHAR},
dept_code = #{deptCode,jdbcType=VARCHAR}
where id in

  #{item}
