select s.*,b.phone
from  ( select id id, title title, address address, region region, longitude longitude, latitude latitude,
content content, level grade, if(score is null,0,score) score, weight weight, category category,null type
from t_bic_scenic

    status >= 9

        and category = #{category,jdbcType=VARCHAR}



)s
left join t_bic_business b on b.principal_id=s.id

select s.*,b.phone from ( select id id, title title, address address, region region, longitude longitude, latitude latitude, content content, null grade, null score, weight weight, null category, null type from t_bic_scenic
status >= 9
)s left join t_bic_business b on b.principal_id=s.id

SELECT s.*, b.phone FROM ( SELECT id id, title title, address address, region region, longitude longitude, latitude latitude, content content, LEVEL grade, NULL score, weight weight, NULL category, NULL type FROM t_bic_travel_agent
status >= 9
) s LEFT JOIN t_bic_business b ON b.principal_id = s.id

