select s.*,b.phone from ( select id id, title title, address address, region region, longitude longitude, latitude latitude, content content, level grade, if(score is null,0,score) score, weight weight, category category, '' type from t_bic_scenic
status >= 9
and category = #{category,jdbcType=VARCHAR}
)s left join t_bic_business b on b.principal_id=s.id

select s.*,b.phone from ( select id id, title title, address address, region region, longitude longitude, latitude latitude, content content, '' grade, 0 score, weight weight, '125004001' category, '' type from t_bic_scenic
status >= 9
)s left join t_bic_business b on b.principal_id=s.id

SELECT id id, title title, address address, region region, longitude longitude, latitude latitude, phone phone, content content, 0 grade, score score, weight weight, category category, type type FROM t_bic_periphery
status >= 9
and category = #{category,jdbcType=VARCHAR}
