from t_bic_extend



    and principal_id = #{principalId}



    and title like #{title}



    and type = #{type}



      and status = #{status,jdbcType=INTEGER}


      and (status = 1 or status = 9)




      and status = #{onlineStatus,jdbcType=INTEGER}


      and 9 > status

